package ques;

public class Ques7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		   int x = 5;
	        int y = 10;
	        int z;

	        System.out.println("Before swapping:");
	        System.out.println("x = " + x);
	        System.out.println("y = " + y);

	        z = x;
	        x = y;
	        y = z;

	        System.out.println("After swapping:");
	        System.out.println("x = " + x);
	        System.out.println("y = " + y);
	}

}
