package ques;

public class Ques8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 45;
		int y = 74;
	System.out.println("before exchange");
	System.out.println("x=" +x);
	System.out.println("y=" +y);
	x = x + y;
	y = x - y;
	x = x - y;
	System.out.println("after exchange");
	System.out.println("x=" +x);
	System.out.println("y=" +y);
		
	}

}
